import React, { useState } from "react";
import { WoltSurcharge, Currency, Distance } from "../Utils/StaticData";
import { isFloat } from "../Utils/Helpers";

import {
  calculateSurchargeOnCartValue,
  calculateSurchargeOnDistance,
  calculateSurchargeOnItems,
  calculateSurchargeOnRushHours,
  calculateTotalDeliveryCharges,
} from "../BusinessLogic/deliveryFeeCalculator";

const date = new Date().toISOString().split();

const Calculator: React.FC = () => {
  const [deliveryFee, setdeliveryFee] = useState("0");
  const [cartValue, setCartValue] = useState(0);
  const [deliveryDistance, setDeliveryDistance] = useState(0);
  const [amoutOfItems, setAmountOfItems] = useState(0);
  const [orderTime, setOrderTime] = useState("");

  const {
    FreeDeliveryCartValue,
    MinumumFreeCartValue,
    MaximumSurchargeFreeDistanceCovered,
    MaximumSurchargeFreeItems,
    SurchargeOnExtraItem,
    RushHoursDay,
    RushHoursStartTime,
    RushHoursEndTime,
    SurchargeOnRushHours,
    MaximumDeliveryFee,
  } = WoltSurcharge;

  const calculatePrice = (e: React.MouseEvent<HTMLButtonElement>) => {
    if (cartValue >= FreeDeliveryCartValue) {
      setdeliveryFee("0");
      return;
    }

    //check for cart vlaue
    let surchargeOnCartValue = calculateSurchargeOnCartValue(
      MinumumFreeCartValue,
      cartValue
    );

    // if(cartValue < 10){
    //     surchargeOnCartValue = 10 - cartValue;
    // };

    //check for distance
    // var distanceInRounds = deliveryDistance % 500;
    // var baseDistance = deliveryDistance / 500;
    // var surchargeOnDistance = parseInt(baseDistance.toString().split(".")[0]);
    // if(distanceInRounds > 0){
    //     surchargeOnDistance = surchargeOnDistance + 1;
    // }

    let surchargeOnDistance = calculateSurchargeOnDistance(
      MaximumSurchargeFreeDistanceCovered,
      deliveryDistance
    );

    //check for number of items
    var surchargeOnItems = calculateSurchargeOnItems(
      MaximumSurchargeFreeItems,
      SurchargeOnExtraItem,
      amoutOfItems
    );

    // if(amoutOfItems > 4){
    //     var surchargebleItems = amoutOfItems - 4;
    //     var surchargePerItem = 0.50;
    //     surchargeOnItems = surchargebleItems * surchargePerItem;
    // }

    //check for datetime
    // var surchargeOnRushHours = 0;
    // var dateTime = new Date(orderTime);
    // const deliveryDay = dateTime.getUTCDay();
    // const deliveryTime = dateTime.getHours();

    var deliverySum =
      surchargeOnCartValue + surchargeOnDistance + surchargeOnItems;

    // if(deliveryDay === 5 && (deliveryTime>= 15 && deliveryTime<= 19))
    // {
    //     totalDeliveryFee = totalDeliveryFee * 1.1;
    //     surchargeOnRushHours = totalDeliveryFee * 1.1;
    // }

    let surchargeOnRushHours = calculateSurchargeOnRushHours(
      RushHoursDay,
      RushHoursStartTime,
      RushHoursEndTime,
      SurchargeOnRushHours,
      orderTime,
      deliverySum
    );

    // console.log("surchargeOnCartValue", surchargeOnCartValue);
    // console.log("surchargeOnDistance", surchargeOnDistance);
    // console.log("surchargeOnItems", surchargeOnItems);
    // console.log("surchargeOnRushHours", surchargeOnRushHours);
    // console.log(totalDeliveryFee);
    // if(totalDeliveryFee > 15){
    //     totalDeliveryFee = 15;
    // }

    //total Delivery Fee
    let totalDeliveryFee = calculateTotalDeliveryCharges(
      MaximumDeliveryFee,
      surchargeOnCartValue,
      surchargeOnDistance,
      surchargeOnItems,
      surchargeOnRushHours
    );
    setdeliveryFee(
      isFloat(totalDeliveryFee)
        ? totalDeliveryFee.toFixed(2)
        : totalDeliveryFee.toString()
    );
  };

  const setValue = (e: any, setState: any) => {
    setState(e.target.value);
  };

  return (
    <>
      {/* <div>
            <div className='col-sm-4 offset-lg-4 offset-md-4 calborder'>
                <p className='cal-heading'>Delivery Fee Calculator</p>
                <div className='calculator'>
                  <div className='row mb-2'>
                      <label className="col-sm-5 col-form-label">Cart Value</label>
                      <div className="col-sm-5">
                        <input className="form-control"  type="number" step="0.1" min='0' value={cartValue} onChange={(event) => setValue(event, setCartValue)}/>
                      </div>
                      <label className="col-sm-2 col-form-label cal-label">{Currency.Euro}</label>
                  </div>
                  <div className='row mb-2'>
                    <label className="col-sm-5 col-form-label">Delivery Distance</label>
                    <div className="col-sm-5">
                        <input className="form-control"  type="number" min='0' value={deliveryDistance} onChange={(event) => setValue(event, setDeliveryDistance)}/>
                    </div>
                    <label className="col-sm-2 col-form-label cal-label">{Distance.Meters}</label>
                  </div>
                  <div className='row mb-2'>
                    <label className="col-sm-5 col-form-label">Amount of Items</label>
                    <div className="col-sm-5">
                      <input className="form-control" type="number" min='0' value={amoutOfItems} onChange={(event) => setValue(event, setAmountOfItems)}/>
                    </div>
                  </div>
                  <div className='row mb-2'>
                    <label className="col-sm-5 col-form-label">Time</label>
                    <div className="col-sm-5">
                      <input className="form-control"  type="datetime-local" value={orderTime} onChange={(event) => setValue(event, setOrderTime)}/>                          
                    </div>
                  </div>
                  <div className='row mb-2'>
                    <button className="col-sm-6 offset-sm-4 btn-primary btn-round"  onClick={calculatePrice}>Calculate Delivery Price </button>
                  </div>
                  <div className='row'> 
                    <p>Delivery Price: {deliveryFee}{Currency.Euro}</p>
                  </div>
                </div>
                <p className='cal-bottom'></p>         
            </div>
        </div> */}
      <div className="col-sm-4 offset-lg-4">
        <div className="card mt-5">
          <div className="card-header">Delivery Fee Calculator</div>
          <div className="card-body">
            <div className="row mb-2">
              <label className="col-sm-5 col-form-label">Cart Value</label>
              <div className="col-sm-5">
                <input
                  className="form-control"
                  type="number"
                  step="0.1"
                  min="0"
                  value={cartValue}
                  onChange={(event) => setValue(event, setCartValue)}
                />
              </div>
              {Currency.Euro}
            </div>
            <div className="row mb-2">
              <label className="col-sm-5 col-form-label">
                Delivery Distance
              </label>
              <div className="col-sm-5">
                <input
                  className="form-control"
                  type="number"
                  min="0"
                  value={deliveryDistance}
                  onChange={(event) => setValue(event, setDeliveryDistance)}
                />
              </div>
              {Distance.Meters}
            </div>
            <div className="row mb-2">
              <label className="col-sm-5 col-form-label">Amount of Items</label>
              <div className="col-sm-5">
                <input
                  className="form-control"
                  type="number"
                  min="0"
                  value={amoutOfItems}
                  onChange={(event) => setValue(event, setAmountOfItems)}
                />
              </div>
            </div>
            <div className="row mb-2">
              <label className="col-sm-4 col-form-label">Time</label>
              <div className="col-sm-7">
                <input
                  className="form-control"
                  type="datetime-local"
                  min={date.toString()}
                  value={orderTime}
                  onChange={(event) => setValue(event, setOrderTime)}
                />
              </div>
            </div>
          </div>
          <div className="card-footer text-muted">
            <div className="row">
              <div className="col-lg-6">
                <button
                  className="btn btn-outline-primary"
                  onClick={calculatePrice}
                >
                  Calculate Delivery Price{" "}
                </button>
              </div>
              <div className="col-lg-6">
                <p>
                  Delivery Price: {deliveryFee}
                  {Currency.Euro}
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Calculator;
