import React from "react";
import { useField } from "formik";

type InputProps = {
  // The common Part
  className: string;
  type: string;
  value: any;
  onChange?: (e: React.ChangeEvent<HTMLInputElement>) => void;
  labelText: string;
  labelClass: string;
  parentDivClass: string;
} & {
  // The discriminated union
  step?: string;
  min?: string;
};

export const Textfield: React.FC<InputProps> = (props: InputProps) => {
  const [field, meta] = useField(props)
  return (
    <>
      <label className={props.labelClass}>{props.labelText}</label>
      <div className={props.parentDivClass}>
        <input {...props} />
      </div>
    </>
  );
};
