import React, {useState} from 'react';

const Calculator : React.FC = ()=>
 { 
  const [price, setPrice] = useState(0);
  const [cartValue, setCartValue] = useState(0);
  const [deliveryDistance, setDeliveryDistance] = useState(0);
  const [amoutOfItems, setAmountOfItems] = useState(0);
  const [orderTime, setOrderTime] = useState(0);
  
  const calculatePrice = (e : React.MouseEvent<HTMLButtonElement>) => {

    if(cartValue >= 100){
        setPrice(0);
    }

    //check for cart vlaue
    var surchargeOnCartValue = 0;
    
    if(cartValue < 10){
        surchargeOnCartValue = 10 - cartValue;
    };

    //check for distance
    var distanceInRounds = deliveryDistance % 500;
    var baseDistance = deliveryDistance / 500;
    var surchargeOnDistance = parseInt(baseDistance.toString().split(".")[0]);

    console.log("")
    if(distanceInRounds > 0){
        surchargeOnDistance = surchargeOnDistance + 1;
    }

    //check for number of items
    var surchargeOnItems = 0;

    if(amoutOfItems > 4){
        var surchargebleItems = amoutOfItems - 4;
        var surchargePerItem = 0.50;
        surchargeOnItems = surchargebleItems * surchargePerItem;
    }

    //check for datetime
    var surchargeOnRushHours = 0;
    var dateTime = new Date(orderTime);
    const deliveryDay = dateTime.getDay();
    const deliveryTime = dateTime.getTime();

    
    // console.log("surchargeOnCartValue", surchargeOnCartValue);
    // console.log("surchargeOnDistance", surchargeOnDistance);
    // console.log("surchargeOnItems", surchargeOnItems);
    const totalDeliveryFee = surchargeOnCartValue + surchargeOnDistance + surchargeOnItems + surchargeOnRushHours;
    
    console.log(totalDeliveryFee);
    setPrice(totalDeliveryFee);
  }

    const ChangeCartValue = (e:any)=>{
      var cartValue = e.target.value
      console.log(cartValue);
      setCartValue(cartValue);
    }
    const ChangeDeliveryDistance = (e:any)=>{
        var deliveryDistance = e.target.value
        setDeliveryDistance(deliveryDistance);
    }
    const ChangeAmountOfItems = (e:any)=>{
        var amountOfItems = e.target.value
        setAmountOfItems(amountOfItems);
    }
    const ChangeDeliverytime = (e:any)=>{
        var deliveryTime = e.target.value
        console.log(deliveryTime);
        setOrderTime(deliveryTime);
    }

     return (
        <>
            <label>Cart Value</label>
            <input className="form-control"  type="number" value={cartValue} onChange={ChangeCartValue}/> <br />
            <label>Delivery Distance</label>
            <input type="number" value={deliveryDistance} onChange={ChangeDeliveryDistance}/> <br />
            <label>Amount of Items</label>
            <input  type="number" value={amoutOfItems} onChange={ChangeAmountOfItems}/> <br />
            <label>Time</label>
            <input type="datetime-local" value={orderTime} onChange={ChangeDeliverytime}/> <br />
            <button onClick={calculatePrice}>Calculate Delivery Price </button>

            <p>Delivery Price: {price}</p>
        </>
        )
 };

export default Calculator;
