import React from "react";

type props = {
  value: any;
  onChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
  stateChange: any;
};

type InputProps = {
  // The common Part
  className: string;
  type: string;
  value: any;
} & {
  // The discriminated union
  step?: string;
  min: string;
  
};

export const Textfield: React.FC<InputProps> = (props: InputProps) => {
  return <input {...props} />;
};
