import React, {useState} from 'react';
import {calculateSurchargeOnCartValue, calculaeSurchargeOnDistance, calculateSurchargeOnItems
        , calculateSurchargeOnRushHours, calculateTotalDeliveryCharges} from '../BusinessLogic/calculator';

const Calculator : React.FC = ()=>
 { 
  const [price, setPrice] = useState(0);
  const [cartValue, setCartValue] = useState(0);
  const [deliveryDistance, setDeliveryDistance] = useState(0);
  const [amoutOfItems, setAmountOfItems] = useState(0);
  const [orderTime, setOrderTime] = useState('');
  
  const calculatePrice = (e : React.MouseEvent<HTMLButtonElement>) => {

    if(cartValue >= 100){
        setPrice(0);
        return;
    }

    //check for cart vlaue
    let surchargeOnCartValue = calculateSurchargeOnCartValue(10, cartValue);
    
    // if(cartValue < 10){
    //     surchargeOnCartValue = 10 - cartValue;
    // };

    //check for distance
    // var distanceInRounds = deliveryDistance % 500;
    // var baseDistance = deliveryDistance / 500;
    // var surchargeOnDistance = parseInt(baseDistance.toString().split(".")[0]);
    // if(distanceInRounds > 0){
    //     surchargeOnDistance = surchargeOnDistance + 1;
    // }

    let surchargeOnDistance = calculaeSurchargeOnDistance(500, deliveryDistance);

    //check for number of items
    var surchargeOnItems = calculateSurchargeOnItems(4, 0.50, amoutOfItems);

    // if(amoutOfItems > 4){
    //     var surchargebleItems = amoutOfItems - 4;
    //     var surchargePerItem = 0.50;
    //     surchargeOnItems = surchargebleItems * surchargePerItem;
    // }

    //check for datetime
    // var surchargeOnRushHours = 0;
    // var dateTime = new Date(orderTime);
    // const deliveryDay = dateTime.getUTCDay();
    // const deliveryTime = dateTime.getHours();

    var deliverySum = surchargeOnCartValue + surchargeOnDistance + surchargeOnItems;

    // if(deliveryDay === 5 && (deliveryTime>= 15 && deliveryTime<= 19))
    // {
    //     totalDeliveryFee = totalDeliveryFee * 1.1;
    //     surchargeOnRushHours = totalDeliveryFee * 1.1;
    // }

    let surchargeOnRushHours = calculateSurchargeOnRushHours(5,15,19,1.1,orderTime,deliverySum);

    // console.log("surchargeOnCartValue", surchargeOnCartValue);
    // console.log("surchargeOnDistance", surchargeOnDistance);
    // console.log("surchargeOnItems", surchargeOnItems);
    // console.log("surchargeOnRushHours", surchargeOnRushHours);
    // console.log(totalDeliveryFee);
    // if(totalDeliveryFee > 15){
    //     totalDeliveryFee = 15;
    // }

    //total Delivery Fee
    let totalDeliveryFee = calculateTotalDeliveryCharges(15, surchargeOnCartValue, surchargeOnDistance, surchargeOnItems, surchargeOnRushHours);

    setPrice(totalDeliveryFee.toFixed(2));
  }

    const setValue = (e:any, setState: any)=>{
        setState(e.target.value);
    }

     return (
        <>
            <label>Cart Value</label>
            <input className="form-control"  type="number" step="0.1" min='0' value={cartValue} onChange={(event) => setValue(event, setCartValue)}/> <br />
            <label>Delivery Distance</label>
            <input type="number" min='0' value={deliveryDistance} onChange={(event) => setValue(event, setDeliveryDistance)}/> <br />
            <label>Amount of Items</label>
            <input  type="number" min='0' value={amoutOfItems} onChange={(event) => setValue(event, setAmountOfItems)}/> <br />
            <label>Time</label>
            <input type="datetime-local" value={orderTime} onChange={(event) => setValue(event, setOrderTime)}/> <br />
            <button onClick={calculatePrice}>Calculate Delivery Price </button>

            <p>Delivery Price: {price}â‚¬</p>
        </>
        )
 };

export default Calculator;
